const forms=document.forms[0],inputs={username:document.querySelector("input#username"),password:document.querySelector("input#password")};inputs.username.addEventListener("input",(function(){inputs.username.validity.valid?inputs.username.classList.remove("is-invalid"):(inputs.username.nextElementSibling.innerText="Harap masukkan username",inputs.username.classList.add("is-invalid"))})),inputs.password.addEventListener("input",(function(){inputs.password.validity.valid?inputs.password.classList.remove("is-invalid"):(inputs.password.nextElementSibling.innerText="Harap masukkan password",inputs.password.classList.add("is-invalid"))})),forms.addEventListener("submit",(function(event){for(input in inputs)inputs[input].validity.valid&&!inputs[input].classList.contains("is-invalid")||event.preventDefault()}));